library(bit64)

fix_esridate <- function(str) {
  dd <- fromJSON(str)
  fn <- dd$fields %>%
    filter(type == "esriFieldTypeDate") %>%
    .$name

  if (length(fn) == 0) {
    return(dd$features$attributes)
  } else {
    for (aa in fn) {
      str <- gsub(paste0("(\"", aa, "\": +)([0-9]+)"), "\\1\"\\2\"", str)
    }
    dd <- fromJSON(str)$features$attributes
    for (aa in fn) {
      dd[[aa]] <- as_datetime(as.integer64(dd[[aa]]) / 1000)
    }
  }
  dd
}

