dd <- gsub(" ", "_", Sys.time())
fnl <- paste0("raw_data/rc/", names(adsd), "_", dd, ".csv")

mapply(function(dt, nm) write.csv(dt, nm, row.names = FALSE), adsd, fnl, SIMPLIFY = FALSE)

